import React from 'react';

import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';

import { QUERY_SINGLE_RECIPE } from '../utils/queries';

import Auth from '../utils/auth';

const SingleRecipe = () => {
    const { recipeId } = useParams();

    const { loading, data } = useQuery(QUERY_SINGLE_RECIPE, {
        variables: { recipeId: recipeId },
    });

    const recipe = data?.recipe || {};

    console.log(recipe);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="singlerecipepage">
            <div>
                {Auth.loggedIn() ? (
                <>
                    <button>Add to Cookbook</button>
                </>
            ): (
                <>
                    <></>
                </>
            )}
            </div>
            
            <div>
                <h1>{recipe.title}</h1>
                <div className="generalInfo">
                    <p>Number of Servings: {recipe.servings}</p>
                    <p>Total Cook Time: {recipe.totalTime}</p>
                </div>
                <h1>Ingredients</h1>
                <div className="ingredientsList">
                    {recipe.ingredients.map((ingredient) => (
                        <p>{ingredient}</p>
                    ))}
                </div>
                <h1>Directions</h1>
                <div className="directionsList">
                    {recipe.directions.map((direction) => (
                        <p>{direction}</p>
                    ))}
                </div>
            </div>
            
        </div>
    );
};

export default SingleRecipe;
