  const { loading, data } = useQuery(QUERY_RECIPES);
  const recipes = data?.recipes || [];

  // THIS CODES SHOULD REMOVE DUPLICATES
  const uniqueRec = [];
  for (let b = 0; b < recipes.length; b++) {
    let aRecipe = recipes[b];
    // console.log("title");
