    for (let c = 0; c < recipes.length; c++) {
      let bRecipe = recipes[c+1];
      if (aRecipe.title === bRecipe.title) {
        uniqueRec.push(aRecipe);
      }
    };
  };



  const randRecipe = [];
  for (let a = 0; a < 5; a++) {
