import React from 'react';

import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';

import { QUERY_SINGLE_RECIPE } from '../utils/queries';


const SingleRecipe = () => {
    const { recipeId } = useParams();

    const { loading, data } = useQuery(QUERY_SINGLE_RECIPE, {
        variables: { recipeId: recipeId },
    });

    const recipe = data?.recipe || {};

    console.log(recipe);

    if (loading) {
        return <div>Loading...</div>;
    }
    return (
        <div key={recipe._id} className="singlerecipepage">
            <h1>{recipe.title}</h1>
            <div className="generalInfo">
                <p>Number of Servings: {recipe.servings}</p>
                <p>{recipe.totalTime}</p>
            </div>
            <div className="ingredientsList">
                {recipe.ingredients.map((ingredient) => (
                    <p>{ingredient}</p>
                ))}
            </div>
            <div className="directionsList">
                {recipe.directions.map((direction) => (
                    <p>{direction}</p>
                ))}
            </div>
        </div>
    );
};

export default SingleRecipe;
