// import { useQuery } from '@apollo/client';
import { } from '../utils/queries';

const Recipespage = () => {
    // IMPORTANT INFO, WILL HAVE TO MOVE IT TO SOMEWHERE ELSE
    const APP_ID = "b2a265ec";
    const APP_KEY = "a8ce17e2a55b2d4b2d802b1792fb480d";
    // const URL = "https://api.edamam.com/search?q=chicken&app_id=b2a265ec&app_key=a8ce17e2a55b2d4b2d802b1792fb480d&from=0&to=20";

    const [recipes, setRecipes]  = useStates([]);

    useEffect(() => {
        getRecipes();
    }, []);

    // THIS FUNCTION SHOULD GRAB ALL OF THE RECIPES WITHIN THE API DATABASE
    const getRecipes = async () => {
        const response = await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID}&app_key=${APP_KEY}&from=0&to=20`);
        const data = await response.json();
        setRecipes(data.hits);
    };

    return (
        <div>
            <div className="search"> 
                <form className="searchform">
                    <input className="searchbar" type="text"></input>
                    <button className="search-button" type="submit">Search</button>
                </form>
            </div>
            <div classname="recipeslist">
                {}
            </div>
        </div>
    )
        
}

export default Recipespage;
