    query user($username: String!) {
        user(username: $username) {
            _id
            username
            email
            postings {
                _id
                title
                publisher
                condition
                description
                createdAt
            }
        }
    }
`;

export const QUERY_POSTINGS = gql`
    query getPostings {
        postings {
            _id
            title
            postAuthor
            publisher
            condition
            description
            createdAt
        }
    }
`;

export const QUERY_SINGLE_POSTING = gql`
    query getSinlgePost($postId: ID!) {
        posting(postingId: $postingId) {
            _id
            title
            postAuthor
            publisher
            condition
            description
            createdAt
        }
    }
`;

export const QUERY_MYPROFILE = gql`
    query myprofile {
        myprofile {
            _id
            username
            email
            postings {
                _id
                title
                postAuthor
                publisher
                condition
                description
                createdAt
            }
        }
    }
`;
