import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useMutation } from '@apollo/client';

import { ADD_RECIPE } from '../../utils/mutations';
import { QUERY_RECIPES, QUERY_MYPROFILE } from '../../utils/queries';

import Auth from '../../utils/auth';

const RecipeForm = () => {

    const [newRecipe, setNewRecipe] = useState({
        title: '',
        servings: '',
        totalTime: '',
        ingredients: [''],
        directions: [''],
    });

    // const converIngred = (event) => {
    //     const { name, value } = event.target;
    //     let 
    // }

    const [addRecipe, { error, data }] = useMutation(ADD_RECIPE, {
        update(cache, { data: {addRecipe} }) {
            try {
                const { recipes } = cache.readQuery({ query: QUERY_RECIPES });

                cache.writeQuery({
                    query: QUERY_RECIPES,
                    data: { recipes: [addRecipe, ...recipes] },
                });
            } catch (e) {
                console.error(e);
            }

            const { myprofile } = cache.readQuery({ query: QUERY_MYPROFILE });
            cache.writeQuery({
                query: QUERY_MYPROFILE,
                data: { myprofile: { ...myprofile, recipes: [...myprofile.recipes, addRecipe] } },
            });
        },
    });

    const handleChange = (event) => {
        const { name, value } = event.target;

        setNewRecipe({
            ...newRecipe,
            [name]: value,
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const { data } = await addRecipe({
                variables: { ...newRecipe },
            });

            setNewRecipe({
                title: '',
                servings: '',
                totalTime: '',
                ingredients: '',
                directions: '',
            });
            
            Auth.login(data.addRecipe)
        } catch (e) {
            console.error(e);
        }
    };


    return (
        <div className="recipeformsection">
            <form className="recipeform">
                <input placeholder="Recipe Name"></input>
                <input placeholder="Servings"></input>
                <input placeholder="Total Time to Cook"></input>
                <input placeholder="Time to Cook"></input>
                <input placeholder="Ingredients"></input>
                <input placeholder="Instructions"></input>
                <div className="">
                    <button className ="recipeformbutton" type="submit">
                        Add Recipe
                    </button>
                </div>
            </form>
        </div>
    );
}

export default RecipeForm;
